let num =
  ""

let int_from_char x = int_of_char x - int_of_char '0'

let prod_str num =
  let num_list = Array.of_seq (String.to_seq num) in

  let rec prod_list id =
    if id >= Array.length num_list then 1
    else int_from_char num_list.(id) * prod_list (id + 1)
  in

  prod_list 0

let rec max_value l len =
  if l + len - 1 >= String.length num then 0
  else max (prod_str (String.sub num l len)) (max_value (l + 1) len)
;;

print_int (max_value 0 13)
